mapper.select:
SELECT oid,wi.wid,drug_name,state,prchs_num,prchs_user,drug_price,create_time,address FROM order_info oi
left join warehouse_info wi on oi.wid=wi.wid
left join drug_info di on wi.did=di.did



            WHERE drug_name=#{name}


            WHERE prchs_user=#{name}



ORDER BY create_time
limit #{curPage},#{limit}

SELECT COUNT(*) FROM order_info oi
left join warehouse_info wi on oi.wid=wi.wid
left join drug_info di on wi.did=di.did



            WHERE prchs_user=#{name}


            WHERE drug_name=#{name}
