mapper.select:
SELECT drug_name,spl_name,stock,sell_num,ret_num,drug_price,goods_price,wi.did,wid
FROM warehouse_info wi
left join drug_info di on wi.did=di.did
left join supply_info spi on di.did=spi.did
left join supplier_info si on spi.sid=si.sid



            WHERE drug_name=#{name}


            WHERE spl_name=#{name}



limit #{curPage},#{limit}

SELECT COUNT(*) FROM warehouse_info wi left join drug_info di on wi.did=di.did left join supply_info spi on di.did=spi.did left join supplier_info si on spi.sid=si.sid
WHERE drug_name=#{name}
WHERE spl_name=#{name}
